// 
// Toggle a destra che hanno richiesto troppi tacosecondi
// 
$(document).ready((function(){
    $('.politic-toggle').hover(function() {
      $('.cat-dx:not(.politic)').toggleClass('off');
    }, function() {
        $('.cat-dx:not(.politic)').toggleClass('off');
    })
  })
);

